#include "../include/libft.h"

void	*ft_memchr(const void *s, int c, size_t n)
{
	size_t	i;
	char	*temp;

	i = 0;
	temp = (char *)s;
	while (i < n)
	{
		if ((unsigned char)temp[i] == (unsigned char)c)
			return ((char *)s + i);
		i++;
	}
	return (0);
}
